import React from "react";

// import Toast from 'react-bootstrap/Toast';
import Container from 'react-bootstrap/Container';
//import Button from 'react-bootstrap/Button';


function Main() {
  const [todos, setTodos] = React.useState([]);
  const [newTodo, setNewTodo] = React.useState("");

  const saveData = (newTodos) => {
    localStorage.setItem("todos", JSON.stringify(newTodos));
  };

  React.useEffect(() => {
    if (localStorage.getItem("todos")) {
      setTodos(JSON.parse(localStorage.getItem("todos")));
    }
  }, []);

  const onAddTodo = () => {
    if (newTodo.trim()) {
      let newTodos = [...todos, { todo: newTodo.trim(), id: Date.now() }];
      setTodos(newTodos);
      setNewTodo("");
      saveData(newTodos);
    }
  };

  const deleteTodo = (id) => {
    let newTodos = todos.filter((todo) => todo.id !== id);
    setTodos(newTodos);

    saveData(newTodos);
  };

  // ****************** Begin JSX ********************

  return (
    <Container className="p-3">

      <h2 className="text-center">Todo</h2>


              <input
                type="text"
                id="todoInput"
                className="form-control"
                placeholder="add todo"
                value={newTodo}
                onChange={(e) => setNewTodo(e.target.value)}
              />

              <button className="btn btn-primary btn-block" onClick={onAddTodo}>
                {" "}
                Add
              </button>




        <ul id="table">
          {todos.map(todo => ( <li key={todo}>{todo} <button className="btn btn-danger" onClick={() => deleteTodo(todo.id)}>Delete</button> </li>)

          )}
        </ul>

    </Container>
  );
}

  export default Main;
